void showAllBumb (WM_MESSAGE *pMsg)
{
	WM_HWIN hItem;
	int i, j;
	for(i = 0; i < 8; i++)
	{
		for(j = 0; j < 15; j++)
		{
			if(block[i][j].isBumb)
			{
				hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_0 + i * 15 + j);
				BUTTON_SetBitmapEx(hItem, 0, &bm12, 0 ,0);
				block[i][j].isShowed = true;
			}			
		}
	}
	
}
