int countNearBumb(int x, int y)
{
	int count = 0;

	if(block[x-1][y-1].isBumb && (x-1)>=0 && (y-1)>=0)
		count++;
	if(block[x][y-1].isBumb && (y-1)>=0)
		count++;
	if(block[x+1][y-1].isBumb && (x+1)<=7 && (y-1)>=0)
		count++;
	if(block[x-1][y].isBumb && (x-1)>=0)
		count++;
	if(block[x+1][y].isBumb && (x+1)<=7)
		count++;
	if(block[x-1][y+1].isBumb && (x-1)>=0 && (y+1)<=14)
		count++;
	if(block[x][y+1].isBumb && (y+1)<=14)
		count++;
	if(block[x+1][y+1].isBumb && (x+1)<=7 && (y+1)<=14)
		count++;
	
	return count;
}

void findAdjaWhiteBlock(int x, int y, WM_MESSAGE *pMsg)
{
	//WM_HWIN hItem;
	if (!block[x][y].isShowed)
	{
		//hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_0 + x * 15 + y);
		//BUTTON_SetBitmapEx(hItem, 0, &bm11, 0 ,0);
		block[x][y].isShowed = true;
		WhiteBlock--;
		if(!block[x-1][y].isBumb && (x-1)>=0)
		{
			showNearBumbNum(x-1, y, block[x-1][y].BumbNum, pMsg);
			findAdjaWhiteBlock(x-1, y, pMsg);
		}
		if(!block[x+1][y].isBumb && (x+1)<=7)
		{
			showNearBumbNum(x+1, y, block[x+1][y].BumbNum, pMsg);
			findAdjaWhiteBlock(x+1, y, pMsg);
		}
		if(!block[x][y-1].isBumb && (y-1)>=0)
		{
			showNearBumbNum(x, y-1, block[x][y-1].BumbNum, pMsg);
			findAdjaWhiteBlock(x, y-1, pMsg);
		}
		if(!block[x][y+1].isBumb && (y+1)<=14)
		{
			showNearBumbNum(x, y+1, block[x][y+1].BumbNum, pMsg);
			findAdjaWhiteBlock(x, y+1, pMsg);
		}
	}
}

void whichPressed(int x, int y, WM_MESSAGE *pMsg, int ID)
{
	WM_HWIN hItem;
	
	if(!block[x][y].isShowed)
	{
		if(block[x][y].isBumb)
		{
			hItem = WM_GetDialogItem(pMsg->hWin, ID_TEXT_0);
			TEXT_SetText(hItem, "Fail");
			showAllBumb(pMsg);
			hItem = WM_GetDialogItem(pMsg->hWin, ID_TEXT_1);
			TEXT_SetText(hItem, "You get $5 (Participation Award)");
			money += 5;
			hItem = WM_GetDialogItem(pMsg->hWin, ID_EDIT_1);
			sprintf(chMoneyBomb, "$: %d", money);
			EDIT_SetText(hItem, chMoneyBomb);
			mode = 20;
		}
		else
		{
			if(block[x][y].BumbNum != 0) //non-WhitBlock
			{
				block[x][y].isShowed = true;
				showNearBumbNum(x, y, block[x][y].BumbNum, pMsg);
				WhiteBlock--;
			}
			else
			{
				findAdjaWhiteBlock(x, y, pMsg);
			}
			
			if (WhiteBlock == 0) // check win?
			{
				hItem = WM_GetDialogItem(pMsg->hWin, ID_TEXT_0);
				TEXT_SetText(hItem, "Win");
				hItem = WM_GetDialogItem(pMsg->hWin, ID_TEXT_1);
				TEXT_SetText(hItem, "You get $20");
				money += 20;
				sprintf(chMoneyBomb, "$: %d", money);
				hItem = WM_GetDialogItem(pMsg->hWin, ID_EDIT_1);
				EDIT_SetText(hItem, chMoneyBomb);
				mode = 20; // close touch detect
			}
		}
	}
}

void initBlock()
{
	int i, j;
	for(i=0; i<8; i++)
	{
		for(j=0; j<15; j++)
		{
			block[i][j].isBumb = false;
			block[i][j].isShowed = false;
			block[i][j].BumbNum = 0;
		}
	}
}

void placeBomb(WM_MESSAGE *pMsg)
{
	
	int i = set_bump;
	int a, b;
	
	while(i > 0)
	{
		srand(*random);
		a = rand()%8;
		srand(*random);
		b = rand()%15;
		if(block[a][b].isBumb == false)
		{
			i--;
			block[a][b].isBumb = true;
			//
			//hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_0 + a * 15 + b);
			//BUTTON_SetBitmapEx(hItem, 0, &bm12, 0 ,0);
		}
	}
}

void countBumb(WM_MESSAGE * pMsg)
{
	
	int i ,j;
	for(i = 0; i<8; i++)
	{
		for(j = 0; j < 15; j++)
		{
			if(!block[i][j].isBumb)
			{
				block[i][j].BumbNum = countNearBumb(i, j);
				//showNearBumbNum(i, j, block[i][j].BumbNum, pMsg);
			}
		}
	}
}
